import './App.css';
import React, { useEffect, useState } from "react";

function App() {

const [xoffset, setXoffset] = useState(0);
const [yoffset, setYoffset] = useState(0);
const [delta, setDelta] = useState(0.2);

useEffect(()=>{
    moveTitleToDown()
    moveTitleToRight()
},[xoffset, yoffset])

console.log(yoffset)

  const moveTitleToDown = () => {
    setYoffset(yoffset+delta);
  }
  const moveTitleToRight = () => {
    setXoffset(xoffset+delta);
  }
  const moveTitleToLeft = () => {
    setXoffset(xoffset-delta);
  }
  const moveTitleToUp = () => {
    setYoffset(yoffset-delta);
  }

	return (
	<div>
		<h2
		style={{
			position: "absolute",
			left: `${xoffset}px`,
			top: `${yoffset}px`,
		}}
		>
		Bird
		</h2>

		<div style={{ marginTop: "80px" }}>
		<button onClick={(e)=>moveTitleToRight()}>
			Move Title To Right
		</button>
		<button onClick={(e)=>moveTitleToDown()}>
			Move Title To Down
		</button>
		<button onClick={(e)=>moveTitleToLeft()}>
			Move Title To Left
		</button>
		<button onClick={(e)=>moveTitleToUp()}>
			Move Title To Up
		</button>
		</div>
	</div>
	);
}


export default App;
