import './App.css';
import React, { useEffect, useState } from "react";

function App() {

const [xoffset, setXoffset] = useState(100);
const [yoffset, setYoffset] = useState(0);
const [delta, setDelta] = useState(1);

useEffect(()=>{
  setTimeout(()=>{
    if(yoffset<=600){
      moveBirdToDown()
    }
    else{
      console.log("Game over")
    }
   }, 5)

},[yoffset])



  const moveBirdToDown = () => {
    setYoffset(yoffset+delta);
  }
  const moveBirdToRight = () => {
    setXoffset(xoffset+delta);
  }
  const moveBirdToLeft = () => {
    setXoffset(xoffset-delta);
  }
  const jump = () => {
    setYoffset(yoffset-100);
  }

	return (
	<div>
		<h2
		style={{
			position: "absolute",
			left: `${xoffset}px`,
			top: `${yoffset}px`,
		}}
		>
		Bird
		</h2>

    <h2
		style={{
			position: "absolute",
			left: `0px`,
			top: `600px`,
		}}
		>
		--------------------------------------------------------------------------------------------------------------------------------------------------------------------
		</h2>
    <button style={{
			position: "absolute",
			left: `0px`,
			top: `700px`,
      width: `1500px`,
		}} onClick={(e)=>jump()}>Jump</button>
	</div>
	);
}


export default App;
